#include <enet/enet.h>\n\n// Network server initialization\nENetHost* initializeServer() {\n    ENetAddress address;\n    address.host = ENET_HOST_ANY;\n    address.port = 1234;\n    ENetHost* server = enet_host_create(&address, 32, 2, 0, 0);\n    return server;\n}\n\n// Network client initialization\nENetPeer* initializeClient(const char* serverAddress) {\n    ENetHost* client = enet_host_create(NULL, 1, 2, 0, 0);\n    ENetAddress address;\n    enet_address_set_host(&address, serverAddress);\n    address.port = 1234;\n    ENetPeer* peer = enet_host_connect(client, &address, 2, 0);\n    return peer;\n}\n\n// Shut down function for server\nvoid shutdownServer(ENetHost* server) {\n    enet_host_destroy(server);\n}\n\n// Shut down function for client\nvoid shutdownClient(ENetHost* client) {\n    enet_host_destroy(client);\n}
